import React, {useReducer} from 'react'

import instructorReducer from "./instructorReducer";
import instructorContext from "./instructorContext";

import {VERIFICA_INSTRUCTOR} from "../../type"

const InstructorState = props => {

    const initialState = {
        instructores:[
            {
                nombre: "Marcos Escobar",
                ci: 4730472,
                tipo:"admin",
                carrera: "LCIK",
                horas: 20,
                estudiante: "Juan Perez",
                presente: true
            },
            {
                nombre: "Juan Escobar",
                ci: 4730473,
                tipo:"admin",
                carrera: "LCIK2",
                horas: 20,
                estudiante: "Juan Perez2",
                presente: true
            },
            {
                nombre: "Ariel Escobar",
                ci: 4730474,
                tipo:"admin",
                carrera: "INI",
                horas: 20,
                estudiante: "Juan Perez3",
                presente: true
            },
        ],
        pagina:null,

    }

    const [state, dispatch] = useReducer(instructorReducer, initialState)

    //funciones
    const enInstructor = (estado) => {
        dispatch({
            type: VERIFICA_INSTRUCTOR,
            payload: estado
        })
    }

    return(
        <instructorContext.Provider
            value={{
                pagina: state.pagina,
                instructores: state.instructores,

                enInstructor,

            }}
        >
            {props.children}
        </instructorContext.Provider>
    )
}

export default InstructorState;
