import React, {useState, useContext} from "react";

//providers
import StudentContext from "../../contex/students/studentsContext";

const IndexInstructor = () => {

    const contextStudent = useContext(StudentContext)
    const { cargaEstudiante } = contextStudent;

    const [Datos, setDatos] = useState({
        nombre: '', 
        edad: '',
        ciudad: ''
    })

    const [Error, setError] = useState(false)

    const onSubmit = (e) => {
        setDatos({
            ...Datos,
            [e.target.name] : e.target.value
        })
    }

    const onChange = () => {

        if(nombre.trim() === '' || edad.trim() === '' || ciudad.trim() === '' ){
            setError(true)
            return;
        }

        setError(false)
        cargaEstudiante();
    }

    const { nombre, edad, ciudad } = Datos;

    return (
        <div className="container mt-5">
            <div className="row justify-content-center">
                <div className="col-md-10">
                    <div className="card p-5">
                        <h1>Bienvenido Marcos Escobar</h1>
                        <hr/>
                        <h6>Ingresa los datos de tu Estudiante</h6>

                        <form onSubmit={onSubmit}>
                            <div className="form-group">
                                <label>Nombre Completo</label>
                                <input
                                    type="text"
                                    className="form-control mb-4"
                                    placeholder="Ingrese su Nombre"
                                    name="nombre"
                                    value={nombre}
                                    onChange={onChange}
                                />

                                <label>Edad</label>
                                <input
                                    type="text"
                                    className="form-control mb-4"
                                    placeholder="Ingrese su Edad"
                                    name="edad"
                                    value={edad}
                                    onChange={onChange}
                                />

                                <label>Ciudad Actual</label>
                                <input
                                    type="text"
                                    className="form-control mb-4"
                                    placeholder="Ingrese la Ciudad donde Vive"
                                    name="ciudad"
                                    value={ciudad}
                                    onChange={onChange}
                                />

                                <button
                                    type="submit"
                                    className="btn btn-primary font-weight-bold text-uppercase d-block w-100"

                                >
                                    Registrarse
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default IndexInstructor;
