import React, {useReducer} from 'react'

import instructorReducer from "./instructorReducer";
import instructorContext from "./instructorContext";

import {
        VERIFICA_INSTRUCTOR,
        INICIA_SESION,
        REGISTRA_INSTRUCTOR,
        CARGA_ESTUDIANTE,
                         } from "../../type"

const InstructorState = props => {

    const initialState = {
        instructores:[
            {
                id: 1,
                nombre: "Marcos Escobar",
                ci: 4730472,
                tipo:"admin",
                carrera: "LCIK",
                horas: 20,
                estudiante: "Juan Perez",
                presente: true
            },
            {
                id: 2,
                nombre: "Juan Escobar",
                ci: 4730473,
                tipo:"admin",
                carrera: "LCIK2",
                horas: 20,
                estudiante: "Juan Perez2",
                presente: true
            },
            {
                id: 3,
                nombre: "Ariel Escobar",
                ci: 4730474,
                tipo:"admin",
                carrera: "INI",
                horas: 20,
                estudiante: "Juan Perez3",
                presente: true
            },
        ],
        instructorAuth: null,
        estudiante: null,
        autenticado: null,
        pagina:null,

    }

    const [state, dispatch] = useReducer(instructorReducer, initialState)

    //funciones
    const enInstructor = (estado) => {
        dispatch({
            type: VERIFICA_INSTRUCTOR,
            payload: estado
        })
    }

    const iniciaSesion = (instructor) => {

        dispatch({
            type: INICIA_SESION,
            payload: instructor
        })
    }

    const registrarse = instructor => {
        dispatch({
            type: REGISTRA_INSTRUCTOR,
            payload: instructor
        })
    }

    const cargaEstudiantes = datos => {
        dispatch({
            type: CARGA_ESTUDIANTE,
            payload: datos
        })
    }

    return(
        <instructorContext.Provider
            value={{
                pagina: state.pagina,
                instructores: state.instructores,
                instructorAuth: state.instructorAuth,
                estudiante: state.estudiante,

                enInstructor,
                iniciaSesion,
                registrarse,
                cargaEstudiantes,

            }}
        >
            {props.children}
        </instructorContext.Provider>
    )
}

export default InstructorState;
