import React, {useContext} from 'react';
import { Map, TileLayer, Popup } from 'react-leaflet';
import mapaContext from "../../contex/mapa/mapaContex";



const styles = {
  wrapper: { 
    height: 400, 
    width: '100%', 
    margin: '0 auto', 
    display: 'flex' ,
    borderRadius: 10
  },
  map: {
    flex: 1
  } 
};


const Moves = props => {

  const contextMapa = useContext(mapaContext);

  return (
    <div style={styles.wrapper}>
      <Map 
      style={styles.map}
      center={props.center}
    zoom={props.zoom}
    >
    <Popup
    position={[25.2728675,-57.5666722]}
    >
        <div>
            <h2>Lugar</h2>
            <p>Descripcion</p>
        </div>
    </Popup>


        <TileLayer url={props.url} />
      </Map>
    </div>
  );
}

Moves.defaultProps = {
  center: [-23.3601939, -62.9459786],
  zoom: 10,
  url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png'
};

export default Moves;
