import React, {useContext, useEffect, useState} from "react";

import mapaContext from "../../contex/mapa/mapaContex"

const Registro = () => {


    const contextMapa = useContext(mapaContext);
    const {verificaLugar} = contextMapa;

    const [Datos, setDatos] = useState({
        nombre: '', 
        edad: '',
        ciudad: ''
    })

    const onChange = (e) => {
        setDatos({
            ...Datos,
            [e.target.name] : e.target.value
        })

    }

    const onSubmit = e => {
        e.preventDefault();


    }
    useEffect(() => {
        verificaLugar(true)
    }, [])

    return (
        <div className="row justify-content-center ">
            <div className="col-md-8 mt-5">
                <div className="card">
                    <div className="card-body">
                        <div className="text-center mb-4 font-weight-bold">
                            Registrar
                        </div>

                        <form>
                            <div className="form-group">
                                <label>Nombre Completo</label>
                                <input
                                    type="text"
                                    className="form-control mb-4"
                                    placeholder="Ingrese su Nombre"
                                    name="nombre"
                                    onChange={onChange}
                                />

                                <label>Edad</label>
                                <input
                                    type="text"
                                    className="form-control mb-4"
                                    placeholder="Ingrese su Edad"
                                    name="edad"
                                    onChange={onChange}
                                />

                                <label>Ciudad Actual</label>
                                <input
                                    type="text"
                                    className="form-control mb-4"
                                    placeholder="Ingrese la Ciudad donde Vive"
                                    name="ciudad"
                                    onChange={onChange}
                                />

                                <button
                                    type="submit"
                                    className="btn btn-primary font-weight-bold text-uppercase d-block w-100"
                                    onSubmit={onSubmit}
                                >
                                    Registrarse
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Registro;
