import {
        VERIFICA_INSTRUCTOR,
        INICIA_SESION,
     } from "../../type"

export default (state, action) => {
    switch (action.type) {

        case VERIFICA_INSTRUCTOR:
            return {
                ...state,
                pagina: action.payload
            }
        case INICIA_SESION:
            return {
                ...state,
                instructorAuth: state.instructores.filter(instructor => instructor.nombre === action.payload.nombre)
            }
        default:
            return state;
    }
}
